import { useState } from "react";

export default function App() {
  return (
    <div>
      <p>Tip Calculator</p>
      <TipCalculator />
    </div>
  );
}

function TipCalculator() {

  const [bill,setBill] = useState(0);
  const [selectPercentage1, setSelectPercentage1] = useState();
  const [selectPercentage2, setSelectPercentage2] = useState();

  return (
    <div>
      <BillInput bill = {bill} onSetBill = {setBill}/>
      <SelectPercentage percentage = {selectPercentage1} onSelect = {setSelectPercentage1}> How do you like the service?</SelectPercentage>
      <SelectPercentage> How do your friend like the Service?</SelectPercentage>
      <Output bill = {bill} />
      <Reset />
    </div>
  );
}

function BillInput({bill, onSetBill}) {

  return(
    <div>
      <label>How Much was the bill?</label>
      <input 
        type = "text"
        placeholder = "Bill value" 
        value = {bill} 
        onChange ={(e) => onSetBill(Number(e.target.value))}/>
    </div>
  );

}

function SelectPercentage({children, percentage, onSelect}) {

  return(
    <div>
      <label>{children}</label>


      <select>
        <option 
        value = {percentage}
        onChange = {(e) => onSelect(e.target.value)}
        >Dissatisfied (0%)</option>
        <option value = "5"> It was okay (5%)</option>
        <option value = "10"> It was good (10%)</option>
        <option value = "20"> Absolutely amazing !(20%)</option>
      </select>
    </div>
  );
}

function Output({bill}) {
  return <h3> You pay x (${bill}+ $B tip)</h3>
}

function Reset() {}
